import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { MenuItemInitial } from '../types';

interface MenuItemFormProps {
  addItem: (item: MenuItemInitial) => void;
  updateItem?: (id: string, item: MenuItemInitial) => void;
  existingItem?: MenuItemInitial;
}

const MenuItemForm: React.FC<MenuItemFormProps> = ({ addItem, updateItem, existingItem }) => {
  const formik = useFormik({
    initialValues: existingItem || {
      category: '',
      name: '',
      options: '',
      price: 0,
      cost: 0,
      stock: 0,
    },
    validationSchema: Yup.object({
      category: Yup.string().required('Required'),
      name: Yup.string().required('Required'),
      price: Yup.number().required('Required').positive(),
      cost: Yup.number().required('Required').positive(),
      stock: Yup.number().required('Required').min(0),
    }),
    onSubmit: (values) => {
      if (existingItem && existingItem.id) {
        updateItem!(existingItem.id, values);
      } else {
        addItem(values);
      }
      formik.resetForm();
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <input
        type="text"
        name="category"
        value={formik.values.category}
        onChange={formik.handleChange}
        placeholder="Category"
      />
      {formik.errors.category ? <div>{formik.errors.category}</div> : null}

      <input
        type="text"
        name="name"
        value={formik.values.name}
        onChange={formik.handleChange}
        placeholder="Name"
      />
      {formik.errors.name ? <div>{formik.errors.name}</div> : null}

      <input
        type="text"
        name="options"
        value={formik.values.options}
        onChange={formik.handleChange}
        placeholder="Options"
      />

      <input
        type="number"
        name="price"
        value={formik.values.price}
        onChange={formik.handleChange}
        placeholder="Price"
      />
      {formik.errors.price ? <div>{formik.errors.price}</div> : null}

      <input
        type="number"
        name="cost"
        value={formik.values.cost}
        onChange={formik.handleChange}
        placeholder="Cost"
      />
      {formik.errors.cost ? <div>{formik.errors.cost}</div> : null}

      <input
        type="number"
        name="stock"
        value={formik.values.stock}
        onChange={formik.handleChange}
        placeholder="Amount in Stock"
      />
      {formik.errors.stock ? <div>{formik.errors.stock}</div> : null}

      <button type="submit">{existingItem ? 'Update' : 'Add'} Item</button>
    </form>
  );
};

export default MenuItemForm;
