import React, { useState } from 'react';
import { MenuItem as MenuItemType, MenuItemInitial } from '../types';
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper,
  Button, Dialog, DialogActions, DialogContent, DialogTitle
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import MenuItemForm from './MenuItemForm';

interface MenuItemListProps {
  items: MenuItemType[];
  addItem: (item: MenuItemInitial) => void;
  updateItem: (id: string, item: MenuItemInitial) => void;
  deleteItem: (id: string) => void;
}

const MenuItemList: React.FC<MenuItemListProps> = ({ items, addItem, updateItem, deleteItem }) => {
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button variant="contained" color="primary" startIcon={<AddIcon />} onClick={handleClickOpen}>
        Add Menu
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add Menu Item</DialogTitle>
        <DialogContent>
          <MenuItemForm addItem={addItem} onClose={handleClose} />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
        </DialogActions>
      </Dialog>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Category</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Options</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Cost</TableCell>
              <TableCell>Stock</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {items.map((item) => (
              <TableRow key={item.id}>
                <TableCell>{item.category}</TableCell>
                <TableCell>{item.name}</TableCell>
                <TableCell>{item.options}</TableCell>
                <TableCell>{item.price}</TableCell>
                <TableCell>{item.cost}</TableCell>
                <TableCell>{item.stock}</TableCell>
                <TableCell>
                  <Button variant="contained" color="primary" onClick={() => updateItem(item.id, item)}>
                    Edit
                  </Button>
                  <Button variant="contained" color="secondary" onClick={() => deleteItem(item.id)}>
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default MenuItemList;
